import React from 'react';
import { Text, View, TextInput, Button, Alert, StyleSheet, Dimensions } from 'react-native';
import formHooks from './forms';

const { width, height } = Dimensions.get('window');

const Login = (props) => {
  const { handleLogin, handleSignup, hasAccount, setHasAccount } = props;

  const { Controller, control, errors, handleSubmit } = formHooks();

  return (
    <View style={styles.container}>
      <View style={styles.loginContainer}>
        <Controller
          control={control}
          rules={{
            required: true,
          }}
          render={({ field: { onChange, onBlur, value } }) => (
            <TextInput onBlur={onBlur} onChangeText={onChange} value={value} placeholder="Email" />
          )}
          name="email"
          defaultValue=""
        />
        {errors.email && <Text>This is required.</Text>}

        <Controller
          control={control}
          rules={{
            required: true,
          }}
          render={({ field: { onChange, onBlur, value } }) => (
            <TextInput
              onBlur={onBlur}
              onChangeText={onChange}
              value={value}
              placeholder="Password"
            />
          )}
          name="password"
          defaultValue=""
        />
        {errors.password && <Text>This is required.</Text>}

        <View style={styles.btnContainer}>
          {hasAccount ? (
            <>
              <Button title="Sign in" onPress={handleSubmit(handleLogin)} />
              <Text>Don't have an account?</Text>
              <Text onPress={() => setHasAccount(!hasAccount)}>Sign up</Text>
            </>
          ) : (
            <>
              <Button title="Sign up" onPress={handleSubmit(handleSignup)} />
              <Text>Have an account?</Text>
              <Text onPress={() => setHasAccount(!hasAccount)}>Sign in</Text>
            </>
          )}
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    minHeight: height / 2 + height / 3,
    flex: 1,
    backgroundColor: 'red',
  },
  loginContainer: {
    padding: 60,
    width: '100%',
    maxWidth: 520,
    minHeight: 600,
    flexDirection: 'column',
    justifyContent: 'center',
  },

  input: {
    width: '100%',
    fontSize: 19,
    padding: 10,
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    color: '#fff',
    letterSpacing: 1,
  },
  buttons: {
    width: '100%',
    paddingTop: 15,
    paddingBottom: 15,
    color: '#fff',
    fontSize: 16,
    letterSpacing: 1,
    backgroundColor: '#603bbb',
  },
  btnContainer: {
    width: '100%',
    paddingTop: 24,
    paddingBottom: 24,
  },
});

export default Login;
