import React, { useState, useEffect } from 'react';
import { View, StyleSheet, Text, SafeAreaView, Dimensions } from 'react-native';
import Login from './components/Login.js';
import Hero from './components/Hero.js';
import { auth } from './fire';
import FormHooks from './components/forms';
import { NativeBaseProvider } from 'native-base';
import StorageStatusBar from './components/StatusBar.js';

const { width, height } = Dimensions.get('window');

const App = () => {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [hasAccount, setHasAccount] = useState(false);

  // const { Controller, control, errors, handleSubmit } = formHooks();

  const clearInputs = () => {
    setEmail('');
    setPassword('');
  };

  const clearErrors = () => {
    setEmailError('');
    setPassword('');
  };

  const handleLogin = (data) => {
    clearErrors();
    auth.signInWithEmailAndPassword(data.email, data.password).catch((err) => {
      switch (err.code) {
        case 'auth/invalid-email':
        case 'auth/user-disable':
        case 'auth/user-not-found':
          setEmailError(err.message);
          break;
        case 'auth/wrong-password':
          setPasswordError(err.message);
          break;
      }
    });
  };

  const handleSignup = (data) => {
    clearErrors();
    auth.createUserWithEmailAndPassword(data.email, data.password).catch((err) => {
      switch (err.code) {
        case 'auth/email-already-in-use':
        case 'auth/invalid-email':
        case 'auth/user-not-found':
          setEmailError(err.message);
          break;
        case 'auth/weak-password':
          setPasswordError(err.message);
          break;
      }
    });
  };

  const handleLogout = () => {
    auth.signOut();
  };

  const authListener = () => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        clearInputs();
        setUser(user);
      } else {
        setUser('');
      }
    });
  };

  useEffect(() => {
    authListener();
  }, []);

  return (
    <>
      <StorageStatusBar backgroundColor={'#111827'} barStyle="light" />
      <NativeBaseProvider>
        <View style={styles.container}>
          {user ? (
            <Hero handleLogout={handleLogout} />
          ) : (
            <Login
              handleLogin={handleLogin}
              handleSignup={handleSignup}
              hasAccount={hasAccount}
              setHasAccount={setHasAccount}
              passwordError={passwordError}
              emailError={emailError}
            />
          )}
        </View>
      </NativeBaseProvider>
    </>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#111827',
  },
});

export default App;
